for task in range(11):
    for routeit, route in enumerate([route_straight, route_leftturn, route_rightturn]): 
        with open('test_log/Train2_1/log_2023-09-27-03-47-33_task_'+ str(task) + '_route_' + str(routeit) + '.json') as f:
            json_object = json.load(f)
        vel = np.array(json_object['vel'])
        distance = np.array(json_object['distance'])

        print("mu_vel : " + str(np.mean(vel)))
        print("std_vel : " + str(np.std(vel)))
        print("mu_dist : " + str(np.mean(distance)))
        print("std_dist : " + str(np.std(distance)))

        with open('test_log/Train2_1/log_2023-09-27-03-47-33_parsed_task_'+ str(task) + '_route_' + str(routeit) + '.txt', 'wt') as f:
            f.write(str(np.mean(vel)) + "\t" + str(np.std(vel)) + "\t" +str(np.mean(distance)) + "\t" +str(np.std(distance)))
        
        traj_map = np.full((512, 1024, 3), 255, np.uint8)
        if task < 9:
            cv2.polylines(traj_map, np.array([np.array(route) * 16. + np.array((-900, 256))], np.int32), False, (0, 0, 0))
            for r in json_object['pos']:
                cv2.polylines(traj_map, np.array([np.array(r[:100]) * 16. + np.array((-900, 256))], np.int32), False, (0, 0, 255))
        else:
            cv2.polylines(traj_map, np.array([np.array(route) * 16. + np.array((-900, 256))], np.int32), False, (0, 0, 0))
            for r in json_object['pos']:
                cv2.polylines(traj_map, np.array([np.array(r) * 16. + np.array((-900, 256))], np.int32), False, (0, 0, 255))
        cv2.imwrite("test_log/Train2_1/log_2023-09-27-03-47-33_parsed_map_" + str(task) + '_route_' + str(routeit) + ".png", traj_map)
                
 